  ip, mac, country, region, 
  city, isp, binding_date,channel_name
  )
values (#{userId,jdbcType=BIGINT}, #{deviceId,jdbcType=VARCHAR}, #{devicePlat,jdbcType=INTEGER}, 
  #{ip,jdbcType=VARCHAR}, #{mac,jdbcType=VARCHAR}, #{country,jdbcType=VARCHAR}, #{region,jdbcType=VARCHAR}, 
  #{city,jdbcType=VARCHAR}, #{isp,jdbcType=VARCHAR}, #{bindingDate,jdbcType=TIMESTAMP}, #{channelName,jdbcType=VARCHAR}
  )
