mapper.select:
select count(1) 
from tb_wz_group


		group_id = #{param.groupId}


		and group_name = #{param.groupName}

		select g.group_id, group_name, group_desc, group_icon, group_leader, g.create_date, leader_percent, group_income, 
    		group_effect, u.username, ug.groupUserNo, u.icon as leaderIcon
		from tb_wz_group g
		join tb_wz_user u on u.user_id=g.group_leader
		left join (
			select count(1) as groupUserNo, group_id
			from tb_wz_user
			group by group_id
		) ug on ug.group_id=g.group_id


				g.group_id = #{param.groupId}


				and g.group_name = #{param.groupName}


		order by group_id desc
		limit #{startPage},#{size}

    select 
	from tb_wz_group


			group_id = #{param.groupId}


			and group_name = #{param.groupName}

    select group_name as name, group_income as gold
	from tb_wz_group
	order by group_income desc limit #{size}

mapper.delete:
update tb_wz_user
set group_id = 0
where group_id = #{groupId}
