mapper.select:
select 
from tb_wz_image_url_setting


		url = #{url,jdbcType=VARCHAR}


		and skip_url = #{skipUrl,jdbcType=VARCHAR}


		and `type` = #{type,jdbcType=SMALLINT}


		and task_id = #{taskId,jdbcType=BIGINT}


		and task_name = #{taskName,jdbcType=VARCHAR}


		and show_ser = #{showSer,jdbcType=INTEGER}

mapper.update:
update tb_wz_image_url_setting 
set show_ser = show_ser - 1 
where show_ser > #{showSer,jdbcType=INTEGER};
