mapper.select:
		select * from tb_wz_news


				id = #{id, #jdbcType=BIGINT}


				and title like concat(concat('%', #{title, jdbcType=VARCHAR}),'%') 


				and news_screenlock = #{newsScreenLock, jdbcType=VARCHAR} 


				and news_url = #{newsUrl, jdbcType=VARCHAR}



 			limit #{start},#{size}

   	 	select  count(*) from tb_wz_news


				id = #{id, #jdbcType=BIGINT}


				and title like concat(concat('%', #{title, jdbcType=VARCHAR}),'%') 


				and news_screenlock = #{newsScreenLock, jdbcType=VARCHAR} 


				and news_url = #{newsUrl, jdbcType=VARCHAR}

mapper.update:
		update tb_wz_news


				title = #{title,jdbcType=VARCHAR},


	      		news_url = #{newsUrl,jdbcType=VARCHAR},


      			news_screenlock = #{newsScreenLock,jdbcType=VARCHAR}


    	where id = #{id,jdbcType=BIGINT}
